import React, { useState } from "react";
import "./booking.css";
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap";
import { useNavigate } from "react-router-dom";

const Booking = ({ tour, avgRating }) => {
  const { price, reviews } = tour;
  const navigate = useNavigate();

  const today = new Date();
  const yyyy = today.getFullYear();
  let mm = today.getMonth() + 1; // Months start at 0!
  let dd = today.getDate();

  if (dd < 10) dd = "0" + dd;
  if (mm < 10) mm = "0" + mm;

  const formattedToday = ('2005' + "/" + '04' + "/" + '20');
  // const formattedToday = dd + "/" + mm + "/" + yyyy;
  console.log(formattedToday)

  const [credentials, setCredentials] = useState({
    userId: "01", //* static for now later convert to dynamic
    userEmail: "user1@email.com",
    fullname: "",
    phone: "",
    guestSize: 1,
    bookAt: ""
  });
  const handleChange = (e) => {
    setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  // *Send data to the server
  const handleClick = (e) => {
    e.preventDefault();
    // console.log(credentials);
    navigate("/thank-you");
  };

  // * Taking 5 % service charge of each place
  const serviceCrg = Math.trunc(
    Number(Number(price) * 0.05) * Number(credentials.guestSize)
  );
  const totalAmt =
    Number(price) * Number(credentials.guestSize) + Number(serviceCrg);
  return (
    <div className="booking">
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>
          ₹ {price} <span>Per/Person</span>{" "}
        </h3>
        <span className="tour__rating d-flex align-items-center ">
          <i class="ri-star-fill"></i>
          {avgRating === 0 ? null : avgRating} ({reviews?.length})
        </span>
      </div>
      {/* ---Booking Form start--- */}
      <div className="booking__form">
        <h5>Information</h5>
        <Form className="booking__info-form" onSubmit={handleClick}>
          <FormGroup>
            <input
              type="text"
              placeholder="Full Name"
              id="fullname"
              required
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="number"
              placeholder="Phone"
              id="phone"
              required
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup className="d-flex align-items-center gap-3">
            <input
              type="date"
              placeholder=""
              id="bookAt"
              required
              defaultValue={formattedToday}
              onChange={handleChange}
            />
            <input
              type="number"
              placeholder="No Of Guests"
              id="guestSize"
              required
              defaultValue={1}
              onChange={handleChange}
            />
          </FormGroup>
        </Form>
      </div>
      {/* ---Booking Form end--- */}
      <div className="booking__bottom">
        <ListGroup>
          <ListGroupItem className="border-0 px-0">
            <h5 className="d-flex align-items-center gap-1">
              ₹ {price} <i class="ri-close-line"></i> 1 person
            </h5>
            <span>₹ {price}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0">
            <h5>Service Charge</h5>
            <span>₹ {serviceCrg}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0 total">
            <h5>Total</h5>
            <span>₹ {totalAmt}</span>
          </ListGroupItem>
        </ListGroup>

        <Button className="btn primary__btn w-100 mt-4" onClick={handleClick}>
          Book Now
        </Button>
      </div>
    </div>
  );
};

export default Booking;
